    /**
     * Botón confirmar.
     * @param evt 
     */
    private void btnConfirmarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConfirmarActionPerformed
        // TODO add your handling code here:
        String nom = txtNombre.getText();
        int pre = Integer.parseInt(txtPrecio.getText());
        String sk = txtSku.getText();
        int cant = Integer.parseInt(txtCantidad.getText());
        
        Producto p = new Producto(nom,pre,sk,cant);
        int estado = p.agregarProducto();
        
        if (estado > 0){
            JOptionPane.showMessageDialog(rootPane, "Ud Agrego un Producto", "ResultadoBD", JOptionPane.INFORMATION_MESSAGE);
            txtNombre.setText("");
            txtPrecio.setText("");
            txtSku.setText("");
            txtCantidad.setText("");
        } else {
            JOptionPane.showMessageDialog(rootPane, "Error", "Hubo un error al Insertar", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnConfirmarActionPerformed

