    /**
     * Confirmación de salida de sesión.
     * @param evt 
     */
    private void jMenuCerrarSesionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuCerrarSesionActionPerformed
        JFrame frame = new JFrame("Cerrar Sesión");
        if(JOptionPane.showConfirmDialog(frame, "¿Seguro que desea cerrar sesión?", "Cerrar Sesión", JOptionPane.YES_NO_OPTION)==JOptionPane.YES_NO_OPTION){
            pantallaInicio inicio = new pantallaInicio();
            this.setVisible(false);
            inicio.setVisible(true);
        }
    }//GEN-LAST:event_jMenuCerrarSesionActionPerformed

    /**
     * Confirmación de salida del sistema.
     * @param evt 
     */
    private void jMenuSalirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuSalirActionPerformed
        JFrame frame = new JFrame("Salir");
        if(JOptionPane.showConfirmDialog(frame, "¿Seguro que desea salir del sistema?", "Salir", JOptionPane.YES_NO_OPTION)==JOptionPane.YES_NO_OPTION){
            System.exit(0);
        }
    }//GEN-LAST:event_jMenuSalirActionPerformed

